    settingLogEntry : settingLogEntry
};
return newMsg;

var settingLogEntry = 
{
    topic       :   msg.settingLogEntry.topic, 
    userID      :   msg.settingLogEntry.userID,
    device      :   msg.settingLogEntry.device,
    timeStamp   :   msg.settingLogEntry.timeStamp,
    ipInfo      :   msg.payload
}
return {topic: 'settingLogEntry', payload: settingLogEntry};

var projectCookie = msg.req.cookies[global.get('project') + 'Role'];
if(projectCookie == undefined)
{
    msg.cookies = { };
    msg.payload.errorMsg = "Credentials needed";
    return [null,msg];
}
msg.token = projectCookie;
msg.payload['roleToken'] = msg.token;
return [msg,null];

var tokenError = msg.error;
if (tokenError != undefined)
{
    msg.payload.errorMsg = tokenError;
    msg.cookies = { };
    return [null,msg];
}
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        msg.payload.errorMsg = 'Login Expired. Please login again';
        msg.cookies = { };
        return [null,msg];
    }
}
var permittedRole = false;
for (irole = 0; irole < msg.payload.allowedRoles.length; ++irole)
{
    for (itoken = 0; itoken < msg.token.roles.length; ++itoken)
    {
        if (msg.token.roles[itoken] == msg.payload.allowedRoles[irole]) permittedRole = true;
    }
}
if (!permittedRole)
{
    msg.payload.errorMsg = 'Credentials not valid for App';
    msg.cookies = { };
    return [null,msg];
}
msg.payload['profile'] = JSON.stringify(msg.token);
return [msg,null];

