                var level = device.refDistance.value - device.ultraDistanceAvg.value;
//              $('#level').text(level.toFixed(0) + ' cm');
//              $('#waterTemp').text('?');
            }
        }
        function updateWind(device)
        {
            if ((new Date().getTime() - device.timeStamp) < 3600000)
            {
                $('#wind').text(getDirectionText(device.windDirection1.value) + ' ' + device.windSpeed1.value.toFixed(1) + ' m/s');
                $('#gusts').text(getDirectionText(device.windDirection2.value) + ' ' + device.gusts.value.toFixed(1) + ' m/s');
            }
        }
        function updateEspotPrice(device)
        {
             $('#espotPrice').text(device.price.value.toFixed(2) + ' SEK/kW-hr');
        }
        function getDirectionText(wd)
        {
            var dirText = '';           
            if ((  0.0 <= wd) && (wd <=  22.5)) dirText = 'N ';
            if (( 22.5 <  wd) && (wd <=  67.5)) dirText = 'NO';
            if (( 67.5 <  wd) && (wd <= 112.5)) dirText = 'O ';
            if ((112.5 <  wd) && (wd <= 157.5)) dirText = 'SO';
            if ((157.5 <  wd) && (wd <= 202.5)) dirText = 'S ';
            if ((202.5 <  wd) && (wd <= 247.5)) dirText = 'SV';
            if ((247.5 <  wd) && (wd <= 292.5)) dirText = 'V ';
            if ((292.5 <  wd) && (wd <= 337.5)) dirText = 'NV';
            if ((337.5 <  wd) && (wd <= 360.0)) dirText = 'N ';
            return dirText;
        }

