            historyPlotLayout.height = height.toFixed(0);

            $("#status").hide();
            $("#historyChartContainer").show();
            validArchiveData = true;
            historyPlotTrace1.x = [];
            historyPlotTrace1.y = [];
            historyPlotTrace2.x = [];
            historyPlotTrace2.y = [];
            historyPlotTrace3.x = [];
            historyPlotTrace3.y = [];
            historyPlotTrace3.text = [];
            historyPlotTrace4.x = [];
            historyPlotTrace4.y = [];
            historyPlotTrace4.text = [];
            historyPlotTrace5.x = [];
            historyPlotTrace5.y = [];
            var waterTempBeginTime = 1627166453473; //10pm July 24 2021 is 1627158411481
            for (var ii = 0; ii < archiveList[0].trace.length; ++ii)
            {
                var x = archiveList[0].trace[ii].timeStamp;
                var y = archiveList[0].trace[ii][archiveList[0].device.attr[0]];
                if ((x > waterTempBeginTime) && (y > 0))
                {
                    historyPlotTrace1.x.push(x);
                    historyPlotTrace1.y.push(y) ;
                }
            }
            for (var ii = 0; ii < archiveList[1].trace.length; ++ii)
            {
                var x = archiveList[1].trace[ii].timeStamp;
                if (x > waterTempBeginTime)
                {
                    historyPlotTrace2.x.push(x);
                    historyPlotTrace2.y.push(archiveList[1].trace[ii][archiveList[1].device.attr[0]]) ;
                    historyPlotTrace3.x.push(x);
                    historyPlotTrace3.y.push(archiveList[1].trace[ii][archiveList[1].device.attr[1]]) ;
                    historyPlotTrace3.text.push(getDirectionText(archiveList[1].trace[ii][archiveList[1].device.attr[2]]));
                    historyPlotTrace4.x.push(x);
                    historyPlotTrace4.y.push(archiveList[1].trace[ii][archiveList[1].device.attr[3]]) ;
                    historyPlotTrace4.text.push(getDirectionText(archiveList[1].trace[ii][archiveList[1].device.attr[2]]));
                }
            }
            for (var ii = 0; ii < archiveList[0].trace.length; ++ii)
            {
                var x = archiveList[0].trace[ii].timeStamp;
                var ultraDistanceAvg = archiveList[0].trace[ii][archiveList[0].device.attr[1]];
                var refDistance      = archiveList[0].trace[ii][archiveList[0].device.attr[2]];
                if (x > waterTempBeginTime)
                {
                    historyPlotTrace5.x.push(x);
                    historyPlotTrace5.y.push(refDistance - ultraDistanceAvg) ;
                }
            }
