            else
            {
                $("#widgetTable").attr("background",'img/blinky-webcam/blinky-webcam-01.jpg' +'?t=' + new Date().getTime().toString());
            }
        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            switch(msg.topic)
            {
                case 'readings':
                    var device = JSON.parse(pako.inflate(msg.payload, { to: 'string' }));
                    device.timeStamp = new Date().getTime();
                    var time = new Date(device.timeStamp).toLocaleString('en-SE',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit', second: '2-digit'}).replace(',', '');
                    $('#waterTempDate').text('Brantevik');
                    if ((device.deviceType == "blinky-DS18B20")     && (device.name == "Brantevik")) updateWaterTemp(device);
                    if ((device.deviceType == "blinky-wind")     && (device.name == "Brantevik"))
                    {
                        updateAirTemp(device);
                    }
                    break;
                case 'getDevices':
                    if (msg.userID == userID)
                    {
                        var time = new Date().toLocaleString('en-SE',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit', second: '2-digit'}).replace(',', '');
                        $('#waterTempDate').text('Brantevik');
                        updateWaterTemp(msg.payload[0]);
                        updateAirTemp(msg.payload[1]);
                     }
                    break;
                default:
                    break;
            }
        }

// Update Readings Javascript
