        }
        function onWebSocketClose()
        {

        }
        function onWebSocketMessage(msg)
        {
            if (msg.userID == userID)
            {
                switch(msg.topic)
                {
                    case 'getSettingsLog':
                        settingsHistory = msg.payload;
                        displayData();
                        break;
                    case 'permissionError':
                        acknowledgeDialog('Permission', 'Error', msg.payload);
                        break;
                    case 'loginExpired':
                        location.reload();
                        break;
                    default:
                    // code block
                }
            }
        }
        function getData()
        {
            var startDate = new Date($( "#startDate" ).val()).getTime();
            var stopDate = new Date($( "#stopDate" ).val()).getTime();
            settingsHistory = null;
            $("#settingsLogTableBody").remove();
            var actionMsg = 
                {
                    startDate   : startDate,
                    stopDate    : stopDate,
                };
            sendActionMsg('getSettingsLog', 'readDatabase', actionMsg);
        }
        function displayData()
        {
            var markup = '<tbody id="settingsLogTableBody">';
            for (var ii = 0; ii < settingsHistory.length; ++ii)
            {
                markup = markup + addRow(ii);
            }
            markup = markup + '</tbody>';
            $("#settingsLogTable").append(markup);
        }

        function addRow(ii)
        {
            var markup = '<tr>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.url + '</td>';
            markup = markup + '<td>' 
                +  '<button onclick="displayDevice(' + ii.toString() + ')">' + settingsHistory[ii].device.deviceType  + '-' + settingsHistory[ii].device.name + '</button>' 
                + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.ip + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.country + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.region + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.city + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.rdns + '</td>';
            markup = markup + '<td>' + settingsHistory[ii].ipInfo.isp + '</td>';
            markup = markup + '<td>' + new Date(settingsHistory[ii].timeStamp).toLocaleDateString() + '</td>';
            markup = markup + '<td>' + new Date(settingsHistory[ii].timeStamp).toLocaleTimeString() + '</td>';
            markup = markup + '</tr>';
            return markup;

        }
        function displayDevice(index)
        {
            var actionArray = [];
            var attrList = [];
            var keys = Object.keys(settingsHistory[index].device);
            for (var ii = 0; ii < keys.length; ++ii)
            {
                if (settingsHistory[index].device[keys[ii]].hasOwnProperty('type'))
                {
                    if (settingsHistory[index].device[keys[ii]].type == 'scalar')
                    {
                        if (settingsHistory[index].device[keys[ii]].action == 'setting')
                        {
                            actionArray.push(settingsHistory[index].device[keys[ii]]);
                            attrList.push(keys[ii]);
                        }
                    }
                }
            }

            var time = new Date(settingsHistory[index].timeStamp).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');

            $( "#showDeviceDialogName" ).html(settingsHistory[index].device.name + ' ' + time);
            $( "#showDeviceTableBody" ).remove();
            var markup = '<tbody id="showDeviceTableBody">';

            for (var ii = 0; ii < actionArray.length; ++ii)
            {
                markup = markup +'<tr>';
                markup = markup + '<td align="left">' + attrList[ii] + '</td>';
                markup = markup + '<td>' + actionArray[ii].value.toString() + '</td>';
                markup = markup + '</tr>';
            }
            markup = markup + '</tbody>';
            $("#showDeviceTable").append(markup);

            $( "#showDeviceDialog" ).dialog( "option", "title", "Device Setting"); 
            $( "#showDeviceDialog" ).dialog( "open" );
        }
        $( function() 
        {
            $( "#showDeviceDialog" ).dialog(
                {
//                    closeOnEscape: false,
//                    open: function(event, ui) {$(".ui-dialog-titlebar-close", ui.dialog | ui).hide();},
                    width:    640,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Close",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        }
                    ]
                });
        } );

